select 

from KDI_COMPANY


    and COMPANY_NAME = #{companyName,jdbcType=VARCHAR}


    and COMPANY_ACCOUNT = #{companyAccount,jdbcType=VARCHAR}


    and COMPANY_PWD = #{companyPwd,jdbcType=VARCHAR}


    and COMPANY_CODE = #{companyCode,jdbcType=VARCHAR}


    and PAY_TYPE = #{payType,jdbcType=TINYINT}


    and ENTRY_TIME = #{entryTime,jdbcType=TIMESTAMP}


    and MONTH_CODE = #{monthCode,jdbcType=VARCHAR}


    and SEND_SITE = #{sendSite,jdbcType=VARCHAR}


    and IS_DEFAULT = #{isDefault,jdbcType=TINYINT}


    and ORG_ID = #{orgId,jdbcType=BIGINT}


select count(*) 
from KDI_COMPANY
where ID = #{id,jdbcType=BIGINT}


